#! /usr/bin/env node

const program = require('commander');
const { version } = require('../package.json');
const updateChangeLog = require('../packages/commands/changelog');
const generateRobotMsg = require('../packages/commands/robot-msg');

program.version(version, '-v, --version');

program
  .command('changelog')
  .description('生成项目 changelog')
  .action(() => {
    updateChangeLog();
  });

program
  .command('robot-msg')
  .description('生成企微机器人消息模板内容')
  .action(() => {
    generateRobotMsg();
  });

program.parse(process.argv);
